import { createContext, useEffect, useState } from 'react';
import { useForm } from '../hook/useForm';

export const PokemonContext = createContext();

export const PokemonProvider = ({ children }) => {
  const [allPokemons, setAllPokemons] = useState([]);
  const [globalPokemons, setGlobalPokemons] = useState([]);
  const [offset, setOffset] = useState(0);
  const [loading, setLoading] = useState(true);
  const [filteredPokemons, setFilteredPokemons] = useState([]);
  const { valueSearch, onInputChange, onResetForm } = useForm({ valueSearch: '' });

  const baseURL = 'https://pokeapi.co/api/v2/';

  const getAllPokemons = async (limit = 50) => {
    try {
      const res = await fetch(`${baseURL}pokemon?limit=${limit}&offset=${offset}`);
      if (!res.ok) throw new Error(`Failed to fetch Pokemon list. Status: ${res.status}`);
      const data = await res.json();

      const promises = data.results.map(async (pokemon) => {
        const res = await fetch(pokemon.url);
        if (!res.ok) throw new Error(`Failed to fetch Pokemon details. Status: ${res.status}`);
        const data = await res.json();
        return data;
      });

      const results = await Promise.all(promises);
      setAllPokemons((prev) => [...prev, ...results]);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching Pokemon list:', error);
      setLoading(false);
    }
  };

  const getGlobalPokemons = async () => {
    try {
      const res = await fetch(`${baseURL}pokemon?limit=100000&offset=0`);
      if (!res.ok) throw new Error(`Failed to fetch global pokemons. Status: ${res.status}`);
      const data = await res.json();

      const promises = data.results.map(async (pokemon) => {
        const res = await fetch(pokemon.url);
        const data = await res.json();
        return data;
      });

      const results = await Promise.all(promises);
      setGlobalPokemons(results);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching global pokemons:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    getAllPokemons();
  }, [offset]);

  useEffect(() => {
    getGlobalPokemons();
  }, []);

  const onClickLoadMore = () => {
    setOffset(offset + 50);
  };

  return (
    <PokemonContext.Provider
      value={{
        valueSearch,
        onInputChange,
        onResetForm,
        allPokemons,
        globalPokemons,
        getPokemonByID: async (id) => {
          const res = await fetch(`${baseURL}pokemon/${id}`);
          const data = await res.json();
          return data;
        },
        onClickLoadMore,
        loading,
        setLoading,
        filteredPokemons,
        setFilteredPokemons,
      }}
    >
      {children}
    </PokemonContext.Provider>
  );
};
