import { useEffect, useState } from 'react';
import { useForm } from '../hook/useForm';
import { PokemonContext } from './PokemonContext';

export const PokemonProvider = ({ children }) => {
  const [allPokemons, setAllPokemons] = useState([]);
  const [globalPokemons, setGlobalPokemons] = useState([]);
  const [offset, setOffset] = useState(0);

  const { valueSearch, onInputChange, onResetForm } = useForm({
    valueSearch: '',
  });

 
  const [loading, setLoading] = useState(true);
  const [active, setActive] = useState(false);

  
  const getAllPokemons = async (limit = 50) => {
    const baseURL = 'https://pokeapi.co/api/v2/';
    try {
      const res = await fetch(`${baseURL}pokemon?limit=${limit}&offset=${offset}`);
      if (!res.ok) {
        throw new Error(`Failed to fetch Pokemon list. Status: ${res.status}`);
      }
      const data = await res.json();

      const promises = data.results.map(async (pokemon) => {
        try {
          const res = await fetch(pokemon.url);
          if (!res.ok) {
            throw new Error(`Failed to fetch Pokemon details. Status: ${res.status}`);
          }
          const data = await res.json();
          return data;
        } catch (error) {
          console.error(`Error fetching details for Pokemon with URL ${pokemon.url}:`, error);
          throw error;
        }
      });

      const results = await Promise.all(promises);

      setAllPokemons((prevPokemons) => [...prevPokemons, ...results]);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching Pokemon list:', error);
      setLoading(false);
    }
  };


  const getGlobalPokemons = async () => {
    const baseURL = 'https://pokeapi.co/api/v2/';
    try {
      const res = await fetch(`${baseURL}pokemon?limit=100000&offset=0`);
      if (!res.ok) {
        throw new Error(`Failed to fetch global pokemons. Status: ${res.status}`);
      }
      const data = await res.json();

      const promises = data.results.map(async (pokemon) => {
        const res = await fetch(pokemon.url);
        const data = await res.json();
        return data;
      });

      const results = await Promise.all(promises);

      setGlobalPokemons(results);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching global pokemons:', error);
      setLoading(false);
    }
  };


  const getPokemonByID = async (id) => {
    const baseURL = 'https://pokeapi.co/api/v2/';
    const res = await fetch(`${baseURL}pokemon/${id}`);
    const data = await res.json();
    return data;
  };

  useEffect(() => {
    getAllPokemons();
  }, [offset]);

  useEffect(() => {
    getGlobalPokemons();
  }, []);

  const onClickLoadMore = () => {
    setOffset(offset + 50);
  };

  const [filteredPokemons, setFilteredPokemons] = useState([]);

  const handleCheckbox = (e) => {
    if (e.target.checked) {
      const filteredResults = globalPokemons.filter((pokemon) =>
        pokemon.types.map((type) => type.type.name).includes(e.target.name)
      );
      setFilteredPokemons((prevFiltered) => [...prevFiltered, ...filteredResults]);
    } else {
      const filteredResults = filteredPokemons.filter(
        (pokemon) => !pokemon.types.map((type) => type.type.name).includes(e.target.name)
      );
      setFilteredPokemons([...filteredResults]);
    }
  };

  return (
    <PokemonContext.Provider
      value={{
        valueSearch,
        onInputChange,
        onResetForm,
        allPokemons,
        globalPokemons,
        getPokemonByID,
        onClickLoadMore,
        // Loader
        loading,
        setLoading,
        // Btn Filter
        active,
        setActive,
        // Filter Container Checkbox
        handleCheckbox,
        filteredPokemons,
      }}
    >
      {children}
    </PokemonContext.Provider>
  );
};
