import React, { useContext } from 'react';
import { useLocation } from 'react-router-dom';
import { PokemonContext } from '../context/PokemonContext';
import { Link } from 'react-router-dom';
import { Loader } from '../components';

export const SearchPage = () => {
  const location = useLocation();
  const { globalPokemons, loading } = useContext(PokemonContext);

  // Ensure searchTerm is properly retrieved from location.state
  const searchTerm = location.state && location.state.searchQuery ? location.state.searchQuery.toLowerCase() : '';

  // Filter pokemons based on searchTerm
  const filteredPokemons = globalPokemons.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(searchTerm)
  );

  return (
    <div className='container'>
      <p className='p-search'>
        {loading ? <Loader/> : `Found ${filteredPokemons.length} results:`}
      </p>
      <div className='card-list-pokemon container'>
        {filteredPokemons.map((pokemon) => (
          <Link
            to={`/pokemon/${pokemon.id}`}
            key={pokemon.id}
            className='card-pokemon'
          >
            <div className='card-img'>
              <img
                src={pokemon.sprites.other.dream_world.front_default}
                alt={`Pokemon ${pokemon.name}`}
              />
            </div>
            <div className='card-info'>
              <span className='pokemon-id'>NÂ° {pokemon.id}</span>
              <h3>{pokemon.name}</h3>
              <div className='card-types'>
                {pokemon.types.map(type => (
                  <span key={type.type.name} className={type.type.name}>
                    {type.type.name}
                  </span>
                ))}
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};
